import React, { useState } from "react";
import { useContext } from "react";
import { FixedSizeList as List } from "react-window";
import { Context } from "../../../context/context";
import ModalDevice from "./ModalDevice";

import cl from "./style.module.css";

const Modal = ({ visible, setVisible }) => {
  const { devices } = useContext(Context);

  const [value, setValuet] = useState("");

  const rootClasses = [cl.modal];
  if (visible) {
    rootClasses.push(cl.active);
  }

  const filteredDevices = devices.filter((device) => {
    return device.toLowerCase().include(value.toLowerCase());
  });

  let list = () => {
    let j = [];
    for (let i = 0; i < devices.length; i++) {
      j[i] = (
        <ModalDevice
          key={devices[i].id}
          device={devices[i]}
          idx={devices[i].id}
        />
      );
    }
    return j;
  };

  const Example = () => (
    <List
      height={500}
      itemCount={devices.length / 10000}
      itemSize={devices.length}
      width={325}
    >
      {list}
    </List>
  );

  return (
    <div
      className={rootClasses.join(" ")}
      onClick={() => {
        setVisible(false);
      }}
    >
      <div className={cl.modelContent} onClick={(e) => e.stopPropagation()}>
        <input
          type="text"
          className={cl.modalInput}
          placeholder="Поиск"
          value={value}
          onChange={(e) => setValuet(e.value.target)}
        />
        {Example()}
      </div>
    </div>
  );
};

export default Modal;
