import React from "react";
import { useContext } from "react";
import { Context } from "../../context/context";
import AddBtn from "./add/AddBtn";
import DeviceItem from "./DeviceItem";
import "./style.css";
import { v4 as uuidv4 } from "uuid";

const Device = () => {
  const { devicesMain, devices } = useContext(Context);

  const list = () => {
    let arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      <DeviceItem key={uuidv4()} device={devicesMain[i]} />;
    }
    if (devicesMain.length > 6) {
      arr.push(<AddBtn />);
    }
    return arr;
  };

  return (
    <>
      <div className="device-list">
        <div className="container">
          <h1 className="title">Смартфоны</h1>
        </div>

        <div className="container">
          <div
            className="check"
            style={{
              marginRight: "50px",
              color: "#0D5ADC",
              marginTop: "100px",
              fontSize: "16px",
            }}
          >
            <input id="check" type={"checkbox"} />
            <label from="check">Показать различия</label>
          </div>
          <div className="devices-list">
            {devicesMain.length === 0 ? <AddBtn /> : list()}
          </div>
        </div>
      </div>
    </>
  );
};

export default Device;
