import React from "react";
import { useContext } from "react";
import { Context } from "../../context/context";
import AddBtn from "./add/AddBtn";
import DeviceItem from "./DeviceItem";
import "./style.css";
import { v4 as uuidv4 } from 'uuid';

const Device = () => {
  const { devicesMain, devices } = useContext(Context);

  return (
    <>
      <div className="device-list">
        <div className="container">
          <h1 className="title">Смартфоны</h1>
        </div>

        <div className="container">
          <div
            style={{
              marginRight: "50px",
              color: "#0D5ADC",
              marginTop: "100px",
              fontSize: "16px",
            }}
          >
            <input id="check" type={"checkbox"} />
            <label from="check">Показать различия</label>
          </div>
          <div className="device-list">
            {devicesMain.length === 0 ? (
              <AddBtn />
            ) : (
              devicesMain.map(dev => <DeviceItem key={uuidv4()} device={dev} />)
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Device;
