import React from "react";
import { useContext } from "react";
import { Context } from "../../context/context";
import {
  InfoProducer,
  InfoYear,
  InfoInch,
  InfoCountry,
  InfoRam,
  InfoSrr,
  InfoNfc,
  InfoESim,
  InfoSWC,
  InfoPrice,
} from "./InfoDetails";
import "./style.css";

const Info = () => {
  const { devicesMain } = useContext(Context);

  const producer = (device) => {
    const arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      arr[i] = <InfoProducer key={i} devicesInfo={device[i]} />;
    }
    return arr;
  };

  const releaseYear = (device) => {
    const arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      arr[i] = <InfoYear key={i} devicesInfo={device[i]} />;
    }
    return arr;
  };

  const inch = (device) => {
    const arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      arr[i] = <InfoInch key={i} devicesInfo={device[i]} />;
    }
    return arr;
  };

  const country = (device) => {
    const arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      arr[i] = <InfoCountry key={i} devicesInfo={device[i]} />;
    }
    return arr;
  };

  const ram = (device) => {
    const arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      arr[i] = <InfoRam key={i} devicesInfo={device[i]} />;
    }
    return arr;
  };

  const srr = (device) => {
    const arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      arr[i] = <InfoSrr key={i} devicesInfo={device[i]} />;
    }
    return arr;
  };

  const nfc = (device) => {
    const arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      arr[i] = <InfoNfc key={i} devicesInfo={device[i]} />;
    }
    return arr;
  };

  const esim = (device) => {
    const arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      arr[i] = <InfoESim key={i} devicesInfo={device[i]} />;
    }
    return arr;
  };

  const swc = (device) => {
    const arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      arr[i] = <InfoSWC key={i} devicesInfo={device[i]} />;
    }
    return arr;
  };

  const price = (device) => {
    const arr = [];
    for (let i = 0; i < devicesMain.length; i++) {
      arr[i] = <InfoPrice key={i} devicesInfo={device[i]} />;
    }
    return arr;
  };

  return (
    <div className="info">
      <div className="container" style={{ borderTop: "1px solid #CDCFD2" }}>
        <div className="table_title">
          <p>Производитель</p>
        </div>

        <div className="info-list">{producer(devicesMain)}</div>
      </div>

      <div className="container" style={{ borderTop: "1px solid #CDCFD2" }}>
        <div className="table_title">
          <p>Год релиза</p>
        </div>

        <div className="info-list">{releaseYear(devicesMain)}</div>
      </div>

      <div className="container" style={{ borderTop: "1px solid #CDCFD2" }}>
        <div className="table_title">
          <p>Диагональ экрана (дюйм)</p>
        </div>

        <div className="info-list">{inch(devicesMain)}</div>
      </div>

      <div className="container" style={{ borderTop: "1px solid #CDCFD2" }}>
        <div>
          <p className="table_title">Страна-производитель</p>
        </div>

        <div className="info-list">{country(devicesMain)}</div>
      </div>

      <div className="container" style={{ borderTop: "1px solid #CDCFD2" }}>
        <div>
          <p className="table_title">Объем памяти</p>
        </div>

        <div className="info-list">{ram(devicesMain)}</div>
      </div>

      <div className="container" style={{ borderTop: "1px solid #CDCFD2" }}>
        <div className="table_title">
          <p>Частота обновления экрана</p>
        </div>

        <div className="info-list">{srr(devicesMain)}</div>
      </div>

      <div className="container" style={{ borderTop: "1px solid #CDCFD2" }}>
        <div>
          <p className="table_title">NFC</p>
        </div>

        <div className="info-list">{nfc(devicesMain)}</div>
      </div>

      <div className="container" style={{ borderTop: "1px solid #CDCFD2", backgroundColor: "red" }}>
        <div className="table_title">
          <p>Поддержка eSIM</p>
        </div>

        <div className="info-list">{esim(devicesMain)}</div>
      </div>

      <div className="container" style={{ borderTop: "1px solid #CDCFD2" }}>
        <div className="table_title">
          <p>Поддержка беспроводной зарядки</p>
        </div>

        <div className="info-list">{swc(devicesMain)}</div>
      </div>

      <div
        className="container"
        style={{
          borderTop: "1px solid #CDCFD2",
          borderBottom: "1px solid #CDCFD2",
        }}
      >
        <div className="table_title">
          <p>Стоимость</p>
        </div>

        <div className="info-list">{price(devicesMain)}</div>
      </div>
    </div>
  );
};

export default Info;
