  //     itemSize={devices.length - count}
  //     width={315}
  //   >
  //     {list}
  //   </List>
  // );

  return (
    <div className={rootClasses.join(" ")} onClick={() => setVisible(false)}>
      <div className={cl.modelContent} onClick={(e) => e.stopPropagation()}>
        <input
          type="text"
          className={cl.modalInput}
          placeholder="Поиск"
        />
        {/* {Example()} */}
      </div>
    </div>
  );
};

export default Modal;
